export const groupBy = (sources, key) => {
    return sources.reduce((s, source) => {
        const value = typeof key === 'function' ? key(source) : source[String(key)];
        if (value !== undefined) {
            if (!s[value]) {
                s[value] = [];
            }
            s[value].push(source);
        }
        return s;
    }, {});
};
