export function walkTree(obj, key, options) {
    const mode = options?.returnType || 'inside';
    const result = {};
    function iter(obj, path) {
        if (!obj || typeof obj !== 'object') {
            return;
        }
        if (key in obj) {
            if (mode === 'inside')
                result[`${path.join('.')}`] = obj[key];
            else
                result[`${path.join('.')}`] = obj;
            return path;
        }
        for (const k in obj) {
            iter(obj[k], [...path, k]);
        }
        return;
    }
    iter(obj, []);
    return result;
}
