export const toCamelCase = (s, hardcore = true) => {
    if (s === null || s === undefined)
        return '';
    if (hardcore) {
        return `${s}`
            .toLowerCase()
            .replace(/(?:^\w|[A-Z]|\b\w)/g, (word, index) => index === 0 ? word.toLowerCase() : word.toUpperCase())
            .replace(/\s+/g, '')
            .replace(/([-_][a-z])/gi, ($1) => {
            return $1.toUpperCase().replace('-', '').replace('_', '');
        })
            .replace(/[^a-zA-Z]/g, '');
    }
    return `${s}`
        .toLowerCase()
        .replace(/[\(\)\]\[]/g, '')
        .replace(/(?:^\w|[A-Z]|\b\w)/g, (word, index) => index === 0 ? word.toLowerCase() : word.toUpperCase())
        .replace(/\s+/g, '')
        .replace(/([-_][a-z])/gi, ($1) => {
        return $1.toUpperCase().replace('-', '').replace('_', '');
    });
};
