import dayjs from 'dayjs';
const nights = (_, dayjsClass) => {
    dayjsClass.prototype.nights = function nights(end) {
        return getNights(this, end);
    };
};
dayjs.extend(nights);
export function getNights(start = new Date(), end = new Date()) {
    return Math.abs(Math.floor(dayjs(start).startOf('day').diff(dayjs(end).startOf('day'), 'day')));
}
