export function expander(exp = '') {
  const keys =
    exp?.split(',').map((x) => x.trim().toLowerCase().split('-').join('')) ||
    [];
  const cache = {};
  return (key: string) => {
    if (cache[key] === undefined) {
      cache[key] =
        exp === '*' || keys.includes(key.split('-').join('').toLowerCase());
    }
    return cache[key];
  };
}
